package com.example.renerubio.solicituddecredito2.Model;public class Localidad {    int asentaiento_id;    String asentamiento;    public Localidad() {        this.asentaiento_id = 0;        this.asentamiento ="";    }    public Localidad(int asentaiento_id,String asentamiento){        this.asentaiento_id = asentaiento_id;        this.asentamiento = asentamiento;    }    public int getAsentaiento_id() {        return asentaiento_id;    }    public void setAsentaiento_id(int asentaiento_id) {        this.asentaiento_id = asentaiento_id;    }    public String getAsentamiento(){return  asentamiento;}    public void setAsentamiento(String asentamiento){this.asentamiento = asentamiento;}}